package mpt

import (
	"github.com/joeqian10/neo3-gogogo/crypto"
	"github.com/stretchr/testify/assert"
	"testing"

	"github.com/joeqian10/neo3-gogogo/helper"
)

func TestVerifyProof(t *testing.T) {
	proofStr := ""
	proofData, err := crypto.Base64Decode(proofStr)
	assert.Nil(t, err)

	root, _ := helper.UInt256FromString("0x7bf925dbd33af0e00d392b92313da59369ed86c82494d0e02040b24faac0a3ca")

	id, key, proofs, err := ResolveProof(proofData)
	assert.Nil(t, err)

	value, err := VerifyProof(root, id, key, proofs)
	assert.Nil(t, err)

	assert.Equal(t, "AAI=", crypto.Base64Encode(value))
}
